query MarkdownByPath(
  $path: String!
) {
  site: site {
    siteMetadata {
      title
      editBaseUrl
    }
  }
  doc: markdownRemark(frontmatter: {path: {eq: $path}}) {
    html
    fileAbsolutePath
    frontmatter {
      title
      content {
        childMarkdownRemark {
          html
          fileAbsolutePath
        }
      }
    }
  }
}

sq--src-layouts-documentation-documentat.query:
query Navigation {
  site {
    siteMetadata {
      title
    }
  }
  navigation: allMarkdownRemark(filter: {frontmatter: {path: {ne: "/404.html"}}}, sort: {fields: [fileAbsolutePath], order: ASC}) {
    docs: edges {
      node {
        frontmatter {
          title
          path
          tag
          content {
            childMarkdownRemark {
              html
              headings(depth: h2) {
                value
              }
            }
          }
        }
        headings(depth: h2) {
          value
        }
      }
    }
  }
}
